import React, { useState } from 'react';
import { ChevronDown, ChevronRight, Clock, CheckCircle, XCircle, Tool } from 'lucide-react';

const ToolCallDisplay = ({ toolCalls = [] }) => {
  const [expandedCalls, setExpandedCalls] = useState(new Set());

  const toggleExpanded = (index) => {
    const newExpanded = new Set(expandedCalls);
    if (newExpanded.has(index)) {
      newExpanded.delete(index);
    } else {
      newExpanded.add(index);
    }
    setExpandedCalls(newExpanded);
  };

  if (!toolCalls || toolCalls.length === 0) {
    return null;
  }

  return (
    <div className="tool-calls-container">
      <div className="tool-calls-header">
        <Tool size={16} />
        <span>工具调用 ({toolCalls.length})</span>
      </div>

      {toolCalls.map((call, index) => {
        const isExpanded = expandedCalls.has(index);
        const isSuccess = call.result?.status === 'success';

        return (
          <div key={index} className="tool-call-item">
            <div 
              className="tool-call-summary"
              onClick={() => toggleExpanded(index)}
            >
              <div className="tool-call-left">
                {isExpanded ? <ChevronDown size={16} /> : <ChevronRight size={16} />}
                <span className="tool-name">{call.tool}</span>
                {isSuccess ? 
                  <CheckCircle size={14} className="status-success" /> : 
                  <XCircle size={14} className="status-error" />
                }
              </div>

              <div className="tool-call-right">
                {call.execution_time && (
                  <span className="execution-time">
                    <Clock size={12} />
                    {(call.execution_time * 1000).toFixed(0)}ms
                  </span>
                )}
              </div>
            </div>

            {isExpanded && (
              <div className="tool-call-details">
                <div className="detail-section">
                  <h4>输入参数:</h4>
                  <pre className="json-display">
                    {JSON.stringify(call.arguments, null, 2)}
                  </pre>
                </div>

                <div className="detail-section">
                  <h4>执行结果:</h4>
                  <pre className="json-display">
                    {JSON.stringify(call.result, null, 2)}
                  </pre>
                </div>

                {call.result?.status === 'success' && call.result?.smiles && (
                  <div className="detail-section">
                    <h4>分子信息:</h4>
                    <div className="molecule-info">
                      <span className="smiles-label">SMILES:</span>
                      <code className="smiles-code">{call.result.smiles}</code>
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
};

export default ToolCallDisplay;
