  onSelectConversation, 
  onNewConversation,
  onDeleteConversation 
}) => {
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diff = now - date;
    const days = Math.floor(diff / (1000 * 60 * 60 * 24));

    if (days === 0) {
      return '‰ªäÂ§©';
