                <div className="conversation-date">
                  {formatDate(conversation.updatedAt)}
                </div>
              </div>
              <button
                className="delete-btn"
                onClick={(e) => {
                  e.stopPropagation();
                  onDeleteConversation(conversation.id);
                }}
                title="Âà†Èô§ÂØπËØù"
              >
