import React, { useState } from 'react';
import { ChevronDown, ChevronRight, Clock, CheckCircle, XCircle, Settings } from 'lucide-react';

const ToolCallDisplay = ({ toolCalls = [] }) => {
  const [expandedCalls, setExpandedCalls] = useState(new Set());

  const toggleExpanded = (index) => {
    const newExpanded = new Set(expandedCalls);
    if (newExpanded.has(index)) {
      newExpanded.delete(index);
    } else {
      newExpanded.add(index);
    }
    setExpandedCalls(newExpanded);
  };

  if (!toolCalls || toolCalls.length === 0) {
    return null;
  }

  return (
    <div className="tool-calls-container">
      <div className="tool-calls-header">
        <Settings size={16} />
        <span>工具调用 ({toolCalls.length})</span>
      </div>

      {toolCalls.map((call, index) => {
        const isExpanded = expandedCalls.has(index);
        return (
          <div key={index} className="tool-call-item">
            <div 
              className="tool-call-summary"
              onClick={() => toggleExpanded(index)}
            >
              <div className="tool-call-left">
                {isExpanded ? <ChevronDown size={16} /> : <ChevronRight size={16} />}
                <span className="tool-name">{call.function.name}</span>
              </div>
            </div>

            {isExpanded && (
              <div className="tool-call-details">
                <div className="detail-section">
                  <h4>输入参数:</h4>
                  <pre className="json-display">
                    {JSON.stringify(JSON.parse(call.function.arguments), null, 2)}
                  </pre>
                </div>
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
};

export default ToolCallDisplay;
