import React, { useState, useRef, useEffect } from 'react';
import { Send, Bot, User, Zap, AlertCircle, Info } from 'lucide-react';
import axios from 'axios';
import ToolCallDisplay from './ToolCallDisplay';

const ChatWindow = ({ 
  conversation, 
  onSendMessage, 
  onUpdateTitle, 
  isLoading, 
  setIsLoading 
}) => {
  const [inputValue, setInputValue] = useState('');
  const messagesEndRef = useRef(null);
  const textareaRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [conversation?.messages]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!inputValue.trim() || isLoading || !conversation) return;

    const userMessage = {
      id: Date.now().toString(),
      type: 'user',
      content: inputValue.trim(),
      timestamp: new Date().toISOString()
    };

    // Add user message
    onSendMessage(conversation.id, userMessage);

    // Update conversation title if it's the first message
    if (conversation.messages.length === 0) {
      const title = inputValue.trim().length > 30 
        ? inputValue.trim().substring(0, 30) + '...'
        : inputValue.trim();
      onUpdateTitle(conversation.id, title);
    }

    const currentInput = inputValue;
    setInputValue('');
    setIsLoading(true);

    try {
      const data = await sendMessage(currentInput, conversation.id, conversation.messages);

      const assistantMessage = {
        id: (Date.now() + 1).toString(),
        type: 'assistant',
        content: data.response,
        timestamp: new Date().toISOString(),
        tool_calls: data.tool_calls || [],
        status: data.status || 'success',
        available_tools: data.available_tools || []
      };

      onSendMessage(conversation.id, assistantMessage);
    } catch (error) {
      console.error('Error sending message:', error);
      const errorMessage = {
        id: (Date.now() + 1).toString(),
        type: 'assistant',
        content: `Êä±Ê≠âÔºåÂèëÁîü‰∫ÜÈîôËØØÔºö${error.detail || error.message}„ÄÇËØ∑Á®çÂêéÂÜçËØï„ÄÇ`,
        timestamp: new Date().toISOString(),
        status: 'error'
      };
      onSendMessage(conversation.id, errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit(e);
    }
  };

  const adjustTextareaHeight = () => {
    const textarea = textareaRef.current;
    if (textarea) {
      textarea.style.height = 'auto';
      textarea.style.height = Math.min(textarea.scrollHeight, 120) + 'px';
    }
  };

  useEffect(() => {
    adjustTextareaHeight();
  }, [inputValue]);

  const formatTime = (timestamp) => {
    return new Date(timestamp).toLocaleTimeString('zh-CN', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (!conversation) {
    return (
      <div className="chat-window">
        <div className="empty-state">
          <div className="empty-state-icon">üß¨</div>
          <h3>Ê¨¢Ëøé‰ΩøÁî® BioaGen Agent</h3>
          <p>
            ËøôÊòØ‰∏Ä‰∏™Âü∫‰∫éAIÁöÑÁîüÁâ©ÂàÜÂ≠êÂàÜÊûêÂä©Êâã„ÄÇÊÇ®ÂèØ‰ª•ËØ¢ÈóÆÊúâÂÖ≥ÂàÜÂ≠êÁªìÊûÑ„ÄÅÊÄßË¥®È¢ÑÊµã„ÄÅ
            ÂåñÂ≠¶ÂèçÂ∫îÁ≠âÁõ∏ÂÖ≥ÈóÆÈ¢ò„ÄÇÁÇπÂáªÂ∑¶‰æß"Êñ∞Âª∫ÂØπËØù"ÂºÄÂßãÊÇ®ÁöÑÂàÜÊûê‰πãÊóÖ„ÄÇ
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="chat-window">
      <div className="chat-header">
        <div className="chat-title">{conversation.title}</div>
      </div>

      <div className="chat-messages">
        {conversation.messages.map((message) => (
          <div key={message.id} className={`message ${message.type}`}>
            <div className="message-avatar">
              {message.type === 'user' ? <User size={16} /> : <Bot size={16} />}
            </div>
            <div className="message-content">
              <div>{message.content}</div>

              {/* Display tool calls if available */}
              {message.tool_calls && message.tool_calls.length > 0 && (
                <ToolCallDisplay toolCalls={message.tool_calls} />
              )}

              {/* Display status indicator */}
              {message.status && message.status !== 'success' && (
                <div className={`message-status ${message.status}`}>
                  {message.status === 'error' && <AlertCircle size={14} />}
                  {message.status === 'warning' && <Info size={14} />}
                  <span>{message.status === 'error' ? 'ÊâßË°åÂ§±Ë¥•' : 'ÈÉ®ÂàÜÊàêÂäü'}</span>
                </div>
              )}

              <div className="message-time">
                {formatTime(message.timestamp)}
              </div>
            </div>
          </div>
        ))}

        {isLoading && (
          <div className="message assistant">
            <div className="message-avatar">
              <Bot size={16} />
            </div>
            <div className="message-content">
              <div className="loading-message">
                Ê≠£Âú®ÊÄùËÄÉ‰∏≠
                <div className="loading-dots">
                  <div className="loading-dot"></div>
                  <div className="loading-dot"></div>
                  <div className="loading-dot"></div>
                </div>
              </div>
            </div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>

      <div className="chat-input-container">
        <form className="chat-input-form" onSubmit={handleSubmit}>
          <textarea
            ref={textareaRef}
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="ËæìÂÖ•ÊÇ®ÁöÑÈóÆÈ¢ò...‰æãÂ¶ÇÔºöÂàÜÊûê‰πôÈÜáÂàÜÂ≠êÁöÑÊÄßË¥®"
            className="chat-input"
            rows={1}
            disabled={isLoading}
          />
          <button
            type="submit"
            className="send-btn"
            disabled={!inputValue.trim() || isLoading}
          >
            <Send size={16} />
            ÂèëÈÄÅ
          </button>
        </form>
      </div>
    </div>
  );
};

export default ChatWindow;
